// src/components/Home.js
import React, { useState, useEffect } from 'react';
import '../styles/Home.css';

const movies = [
  { id: 1, title: 'Interstellar', category: 'Action', price: 10, image: 'data:image/jpeg;base64,', rating: 4.5, description: 'An action-packed thriller.', availableSeats: 5 },
  { id: 2, title: 'Inception', category: 'Sci-Fi', price: 12, image: 'data:image/jpeg;base64,', rating: 4.8, description: 'A mind-bending sci-fi thriller.', availableSeats: 3 },
  { id: 3, title: 'The Dark Knight', category: 'Drama', price: 15, image: 'data:image/jpeg;base64,', rating: 4.9, description: 'A gripping superhero saga.', availableSeats: 2 },
  { id: 4, title: 'Dunkirk', category: 'War', price: 11, image: 'data:image/jpeg;base64,', rating: 4.6, description: 'A powerful WWII drama.', availableSeats: 8 },
];

const languages = ['English', 'Spanish', 'French', 'German'];
const theaters = ['KG Cinemas', 'Broadway Cinemas', 'PVR Mall', 'Brooks'];

function Home() {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [numberOfTickets, setNumberOfTickets] = useState(1);
  const [showTime, setShowTime] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [selectedTheater, setSelectedTheater] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showMessage, setShowMessage] = useState(false); // For fade-in effect

  useEffect(() => {
    if (message) {
      setShowMessage(true);
      const timer = setTimeout(() => {
        setShowMessage(false);
        setMessage('');
      }, 4000); // Message disappears after 4 seconds
      return () => clearTimeout(timer);
    }
  }, [message]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    
    if (selectedMovie && numberOfTickets <= selectedMovie.availableSeats) {
      const totalCost = selectedMovie.price * numberOfTickets;
      setMessage(`Successfully booked ${numberOfTickets} ticket(s) for "${selectedMovie.title}" at ${showTime} in ${selectedLanguage} at ${selectedTheater}. Total cost: $${totalCost}.`);
      selectedMovie.availableSeats -= numberOfTickets; // Update available seats
    } else {
      setMessage('Please select a movie and ensure enough seats are available.');
    }

    setLoading(false);
  };

  const clearSelection = () => {
    setSelectedMovie(null);
    setNumberOfTickets(1);
    setShowTime('');
    setSelectedLanguage('');
    setSelectedTheater('');
    setMessage('');
  };

  return (
    <div className="home">
      <h2>Book Your Tickets</h2>
      <div className="movie-list">
        {movies.map((movie) => (
          <div key={movie.id} className="movie-card">
            <input
              type="radio"
              id={`movie-${movie.id}`}
              name="movie"
              onChange={() => setSelectedMovie(movie)}
            />
            <label htmlFor={`movie-${movie.id}`} className="movie-label">
              <div className="movie-image" style={{ backgroundImage: `url(${movie.image})` }}></div>
              <div className="movie-details">
                <h3>{movie.title}</h3>
                <p><strong>Category:</strong> {movie.category}</p>
                <p><strong>Price:</strong> ${movie.price}</p>
                <p><strong>Rating:</strong> {movie.rating} / 5</p>
                <p><strong>Available Seats:</strong> {movie.availableSeats}</p>
                <p className="movie-description">{movie.description}</p>
              </div>
            </label>
          </div>
        ))}
      </div>
      <form className="booking-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="numberOfTickets">Number of Tickets</label>
          <input
            type="number"
            id="numberOfTickets"
            value={numberOfTickets}
            onChange={(e) => setNumberOfTickets(Math.min(e.target.value, selectedMovie?.availableSeats))}
            min="1"
            max={selectedMovie ? selectedMovie.availableSeats : 1}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="showTime">Select Show Time</label>
          <select id="showTime" value={showTime} onChange={(e) => setShowTime(e.target.value)} required>
            <option value="">Select Show Time</option>
            <option value="1:00 PM">1:00 PM</option>
            <option value="4:00 PM">4:00 PM</option>
            <option value="7:00 PM">7:00 PM</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="selectedLanguage">Select Language</label>
          <select id="selectedLanguage" value={selectedLanguage} onChange={(e) => setSelectedLanguage(e.target.value)} required>
            <option value="">Select Language</option>
            {languages.map((language, index) => (
              <option key={index} value={language}>{language}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="selectedTheater">Select Theater</label>
          <select id="selectedTheater" value={selectedTheater} onChange={(e) => setSelectedTheater(e.target.value)} required>
            <option value="">Select Theater</option>
            {theaters.map((theater, index) => (
              <option key={index} value={theater}>{theater}</option>
            ))}
          </select>
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Booking...' : 'Book Tickets'}
        </button>
        <button type="button" onClick={clearSelection}>Clear Selection</button>
      </form>
      {showMessage && <div className={`confirmation-message fade-in`}>{message}</div>}
    </div>
  );
}

export default Home;
